import Card from '../components/card'
import Layout from '../components/layout'
import fetch from 'node-fetch'
import { useState } from 'react'
import Link from 'next/link'




function HomePage() {
  const [data, setData] = useState("")


  return (
  <Layout>

  <section className="hero">
    <div className="hero-body hero-body-padding-large">
      <div className="container has-text-centered">
        <h1 className="title has-text-white">
          Welcome to Wanchain.guide
        </h1>
        <h2 className="subtitle has-text-white">
          This website features video guides and instructions for Wanchain software
        </h2>
      </div>
    </div>
  </section>
  <section>
    <div className="home">
      <Link href="/lightwallet">
        <a className="preview-link">
          <img className="preview-image" src="/light-wallet.png" 
            alt="Light Wallet Guide"/>
        </a> 
      </Link>
      <Link href="/crosschain">
        <a className="preview-link">
          <img className="preview-image" src="/cross-chain.png"
            alt="Crosschain Transaction Guide"/>
        </a> 
      </Link>
      <Link href="/dappstore">
        <a className="preview-link">
          <img className="preview-image" src="/dapp-store.png"
            alt="DApp Store Guide"/>
        </a> 
      </Link>
      <Link href="/lightwallet">
        <a className="preview-link">
          <img className="preview-image" src="/light-wallet.png"
            alt="Light Wallet Guide"/>
        </a> 
      </Link>
      <Link href="/mywanwallet">
        <a className="preview-link">
          <h1></h1>
          <img className="preview-image" src="/mww.png"
            alt="MyWanWallet Guide"/>
        </a> 
      </Link>
      <Link href="/staking">
        <a className="preview-link">
          <img className="preview-image" src="/staking.png"
            alt="Staking Guide"/>
        </a> 
      </Link>
    </div>
  </section>
  <style jsx>{`
    .title {
      text-align: center;
      margin-bottom: 50px;
    }
    .subtitle {
      max-width: 500px;
      margin: auto;
    }
    .info {
      max-width: 600px;
      margin: 30px auto;
      font-size: 1.3rem;
    }

    .home {
      margin-top: 50px;
      text-align: center;
      margin-bottom: 100px;
    }

    .home p {
      font-size: 2rem;
    }
    .preview-image {
      width: 300px;
      margin: 20px;
      opacity: .5;
      border: 10px solid black;
      border-radius: 20px;
    }

@media (max-width: 1150px) {
  .preview-link {
    display: block;
  }

  .flex-container {
    flex-direction: column;
    position: fixed;
    right: 0px;
    top: 70px;
    background-color: #031464;
    padding: 20px;
    border-radius: 5px;
    z-index: 10;
    border: 3px solid rgb(18,54,100);
    display: none;
  }

}
  `}</style>
  </Layout>
  )
}

export default HomePage
