const VideoGuide = ({pageTitle, videoUrl, videoTitle, info, children}) => {
  return(
    <div>

    <section className="hero">
        <div className="hero-body hero-body-padding-large">
          <div className="container has-text-centered">
          <h1 className="title has-text-white">
            {pageTitle}
          </h1>
            <h2 className="subtitle has-text-white">
              This website features video guides and instructions for Wanchain software
            </h2>
          </div>
        </div>
      </section>

      <p className="info">
        {info}
      </p>
      <p className="info">
        For more information and downloads please navigate to the <a rel="noopener noreferrer" target="_blank" href="https://www.wanchain.org/getstarted/">Wanchain official website</a>.
      </p>

      {children}
      <style jsx>{`
        iframe {
          background-color: black;
        }
        .title {
          text-align: center;
          margin-bottom: 50px;
        }
        .video {
          display: block;
          margin: auto;
          width: 640px;
          height: 360px;
        }
        .info {
          max-width: 600px;
          margin: 30px auto;
          font-size: 1.3rem;
        }

        @media (max-width: 700px) {
          .info {
            width: 300px
          }
          .video {
            display: block;
            margin: auto;
            width: 90vw;
            height: 240px;
          }
        }
      `}</style>
    </div>
  )
}

export default VideoGuide
