const VideoGuide = ({pageTitle, videoUrl, videoTitle, info, children}) => {
  return(
    <div>

    <section className="hero">
        <div className="hero-body hero-body-padding-large">
          <div className="container has-text-centered">
          <h1 className="title has-text-white">
            {pageTitle}
          </h1>
          <iframe className="video" title={videoTitle} src={videoUrl} frameborder="0" allowfullscreen="" id="1419630383"></iframe>

          </div>
        </div>
      </section>



      {children}
      <style jsx>{`
        iframe {
          background-color: black;
        }
        .title {
          text-align: center;
          margin-bottom: 50px;
        }
        .video {
          display: block;
          margin: auto;
          width: 640px;
          height: 360px;
        }
        .info {
          max-width: 600px;
          margin: 30px auto;
          font-size: 1.3rem;
        }

        @media (max-width: 700px) {
          .info {
            width: 300px
          }
          .video {
            display: block;
            margin: auto;
            width: 90vw;
            height: 240px;
          }
        }
      `}</style>
    </div>
  )
}

export default VideoGuide
